import "./styles.css";
import React from "react";
import { FormattedMessage } from "react-intl";
import ReactAnime from "react-animejs";
import ScrollUp from "../ScrollUp";

const Landing = () => {
  const { Anime, stagger } = ReactAnime;

  const fadeInOut = () => {
    setTimeout(() => {
      return "fade-out";
    }, 200);
  };

  return (
    <>
      <div className='h-100 d-flex justify-content-center align-items-center min-vh-100'>
        <ScrollUp />
        <h1 className={`fade-in ${fadeInOut}`}>
          <FormattedMessage id='welcome' />
        </h1>
        <Anime
          initial={[
            {
              targets: "#Box",
              keyframes: [
                {
                  translateX: 50,
                },
                {
                  translateY: 50,
                },
                {
                  translateX: 0,
                },
                {
                  translateY: 0,
                },
              ],
              easing: "spring",
              duration: 3500,
              loop: false,
            },
          ]}
        >
          <div id='Box' style={{ height: 50, width: 50, background: "#d3d" }} />
        </Anime>
      </div>
    </>
  );
};

export default Landing;
