import "./styles.css";
import React, { useEffect } from "react";
import { FormattedMessage } from "react-intl";
import ReactAnime from "react-animejs";
import * as color from "dominant-color";
import imgPath from "../../assets/images/breakingBingo.png";

const Card = (props) => {
  const { anime, stagger } = ReactAnime;
  const options = {
    scale: 2,
    max: 90,
    glare: true,
  };

  useEffect(() => {
    color(imgPath, function (err, color) {
      // hex color by default
      console.log(color); // '5b6c6e'
    });
  }, []);

  return (
    <div className='card container' options={options}>
      <div className='cardContent'>
        <h1>{props.title}</h1>
      </div>
    </div>
  );
};

export default Card;
