import React from "react";
import styled from "styled-components";
import { Controller, Scene } from "react-scrollmagic";
import { Back } from "gsap/gsap-core";
import { Tween, Timeline } from "react-gsap";

const ListStyled = styled.div`
  overflow: hidden;
  .section {
    height: 70vh;
  }
  .devs,
  .devs2 {
    perspective: 4000px;
    left: 400px;
    position: relative;
    & li {
      font-size: 30px;
    }
  }
`;

const Intro2 = () => (
  <ListStyled>
    <div className='section' />
    <Controller>
      <Scene duration={500} offset={300} pin>
        <Tween
          // wrapper={<ul className='devs' />}
          // staggerFrom={{
          //   opacity: 0,
          //   cycle: {
          //     rotationX: [-90, 90],
          //     transformOrigin: ["50% top -100", "50% bottom 100"],
          //   },
          // }}
          // stagger={0.5}
          from={{
            rotationX: -90,
            transformOrigin: "50% top -100",
          }}
          to={{
            rotationX: 0,
            transformOrigin: "50% bottom 100",
          }}
        >
          <li>Rich Harris</li>
          <li>Dan Abramov</li>
          <li>Kyle Simpson</li>
          <li>Gregory Brown</li>
          <li>Addy Osmani</li>
          <li>Evan You</li>
          <li>Axel Rauschmayer</li>
          <li>Sarah Drasner</li>
          <li>Andr√© Staltz</li>
        </Tween>
      </Scene>
    </Controller>
  </ListStyled>
);

export default Intro2;
