import "./styles.css";
import React, { useRef, useEffect } from "react";
import { FormattedMessage } from "react-intl";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import gsap from "gsap";
import Lottie from "react-lottie";
import animationData from "../../assets/animations/developer.json";

const Intro1 = (props) => {
  const defaultOptions = {
    loop: true,
    autoplay: true,
    animationData: animationData,
  };

  useEffect(() => {
    gsap.registerPlugin(ScrollTrigger);

    gsap.fromTo(
      ".hiIm",
      { opacity: 0, translateX: -100 },
      {
        opacity: 1,
        duration: 0.5,
        translateX: 10,
        scrollTrigger: {
          trigger: ".hiIm",
          toggleActions: "play pause reverse pause",
          start: "-1000px",
          markers: true,
          scrub: true,
        },
      }
    );
    // gsap.from(".hiIm", {
    //   translateX: -300,
    //   opacity: 0,
    // });
    // gsap.to(".hiIm", {
    //   scrollTrigger: {
    //     trigger: ".hiIm",
    //     toggleActions: "play pause reverse pause",
    //     start: "center center",
    //     markers: true,
    //     scrub: true,
    //   },
    //   opacity: 1,
    //   translateX: 0,
    //   duration: 5,
    //   ease: "SlowMo",
    // });
  }, []);

  return (
    <div className='vh-100 vw-100 d-flex flex-column align-items-center justify-content-center overflow-hidden'>
      <div className='container row '>
        <div className='col-md-8'>
          <div style={{ textJustify: "inter-word", textAlign: "left" }}>
            <h1
              className='hiIm'
              style={{
                fontWeight: "800",
                fontSize: "4vw",
                textShadow: "0px 0px 10px rgba(47,53,66,0.3)",
              }}
            >
              <FormattedMessage id='helloIm' />
            </h1>
            <h1
              style={{
                fontWeight: "800",
                fontSize: "14vw",
                marginBottom: "-5vw",
                textShadow: "0px 0px 10px rgba(47,53,66,0.3)",
                marginLeft: "-0.35vw",
              }}
            >
              Mohsin
            </h1>
            <h1
              style={{
                fontWeight: "800",
                fontSize: "8vw",
                textShadow: "0px 0px 10px rgba(47,53,66,0.3)",
              }}
            >
              Kamal Akbar
            </h1>
            <br></br>
            <div
              style={{
                width: "150px",
                border: "1px solid #3742fa",
                borderRadius: "10px",
                height: "50px",
              }}
            >
              <p>Download CV</p>
            </div>
          </div>
        </div>
        <div className='col-md-4'>
          <Lottie
            options={defaultOptions}
            height={500}
            width={450}
            isStopped={false}
            style={{ marginTop: "-10vh" }}
          />
        </div>
      </div>
    </div>
  );
};

export default Intro1;
