import "./styles.css";
import React, { useEffect } from "react";
import { FormattedMessage } from "react-intl";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import gsap from "gsap";
import Lottie from "react-lottie";
import animationData from "../../assets/animations/services.json";
import { Timeline } from "gsap/gsap-core";

const Intro3 = (props) => {
  const defaultOptions = {
    loop: true,
    autoplay: true,
    animationData: animationData,
  };

  useEffect(() => {
    gsap.registerPlugin(ScrollTrigger);
    var t1 = new Timeline();

    t1.fromTo(
      ".myTechStack",
      { rotateY: 0 },
      {
        rotateY: -90,
        scrollTrigger: {
          trigger: ".myTechStack",
          toggleActions: "play none none reverse",
          start: "top center",
          end: "bottom center",
          scrub: 1.5,
        },
      }
    );

    gsap.timeline({
      scrollTrigger: {
        trigger: ".hero3",
        pin: ".hero3",
        start: "center center",
        end: "+=2000",
        // scrub: 1,
        // markers: true,
      },
    });

    //ScrollTrigger broke react-router. Below is a fix for it.
    return () => {
      ScrollTrigger.getAll().forEach((instance) => {
        instance.kill();
      });
      gsap.killTweensOf(window);
    };
  }, []);

  return (
    <section
      style={{ height: "100vh" }}
      className='hero3 vw-100 d-flex flex-column align-items-center justify-content-center overflow-hidden'
    >
      <div className='container-fluid row '>
        <div
          className='col-md-7 d-flex  flex-column'
          style={{ marginLeft: "5vw" }}
        >
          <h1
            className='myTechStack'
            style={{
              fontWeight: "800",
              fontSize: "4vw",
              textShadow: "0px 0px 10px rgba(47,53,66,0.3)",
              userSelect: "none",
              transform: "rotateZ(-90)",
            }}
          >
            <FormattedMessage id='MY TECK STACK' />
          </h1>
        </div>
      </div>
    </section>
  );
};

export default Intro3;
