import "./styles.css";
import React, { useEffect, useRef } from "react";
import { FormattedMessage } from "react-intl";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { gsap } from "gsap";
import Lottie from "react-lottie";
import animationData from "../../assets/animations/developer.json";
import { Timeline } from "gsap/gsap-core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFileDownload } from "@fortawesome/free-solid-svg-icons";
import { saveAs } from "file-saver";

gsap.registerPlugin(ScrollTrigger);

const Intro1 = (props) => {
  const defaultOptions = {
    loop: true,
    autoplay: true,
    animationData: animationData,
  };

  const sectionRef = useRef([]);
  sectionRef.current = [];

  const handleDownload = () => {
    //Use the commented part only when you get the CONTENT of the file instead of the whole file...
    // let blob = new Blob([require("../../assets/documents/CV.docx")], {
    //   type: "application/vnd.openxmlformats-officedocument.wordprocessingml.document'",
    // });
    saveAs(
      require("../../assets/documents/resume.pdf"),
      "CV - Mohsin Kamal Akbar.pdf"
    );
  };

  useEffect(() => {
    const sections = document.querySelectorAll("section");

    function goToSection(section, anim) {
      gsap.to(window, {
        scrollTo: { y: section, autoKill: false },
        duration: 1,
      });

      anim && anim.restart();
    }

    sections.forEach((section) => {
      const intoAnim = gsap.from(section.querySelector(".right-col"), {
        yPercent: 50,
        duration: 1,
        paused: true,
      });

      ScrollTrigger.create({
        trigger: section,
        end: "bottom top+=1",
        onEnter: () => goToSection(section, intoAnim),
        onEnterBack: () => goToSection(section),
      });
    });
  }, []);

  // useEffect(() => {
  //   let pinVal = true;
  //   sectionRef.current.forEach((el, index) => {
  //     index === 1
  //       ? gsap.fromTo(
  //           el,
  //           { autoAlpha: 0 },
  //           {
  //             duration: 1,
  //             autoAlpha: 1,
  //             scrollTrigger: {
  //               id: `section${index}`,
  //               trigger: el,
  //               start: "top center",
  //               end: "center",
  //               toggleActions: "play none none reverse",
  //               markers: true,
  //               pin: pinVal,
  //               onLeave: () => !ScrollTrigger.isInViewport(el),
  //             },
  //           }
  //         )
  //       : gsap.fromTo(
  //           el,
  //           { autoAlpha: 0 },
  //           {
  //             duration: 1,
  //             autoAlpha: 1,
  //             scrollTrigger: {
  //               id: `section${index}`,
  //               trigger: el,
  //               start: "top center",
  //               end: "bottom",
  //               toggleActions: "play none none reverse",
  //               markers: true,
  //               pin: pinVal,
  //               onLeave: () => !ScrollTrigger.isInViewport(el),
  //             },
  //           }
  //         );
  //   });
  // }, []);

  const addToRef = (el) => {
    if (el && !sectionRef.current.includes(el)) {
      sectionRef.current.push(el);
    }
  };

  return (
    <>
      <section id='landing-page' class='hero-story hero hero1'>
        <div class='full-col width-100'>
          <h1>Landing Screen</h1>
        </div>
      </section>
      <section id='rooms' class='hero-story hero second-hero hero2'>
        <div class='left-col width-33'>
          <h2>Des chambres pour vous</h2>
        </div>
        <div class='right-col width-66 delayed'>
          <h2>Contenu pour les chambres</h2>
        </div>
      </section>
      <section id='you' class='hero-story hero second-hero hero3'>
        <div class='left-col width-33 delayed'>
          <h2>Contenu gauche pour proche de vous</h2>
        </div>
        <div class='middle-col width-33'>
          <h2>Proche de vous</h2>
        </div>
        <div class='right-col width-33 delayed'>
          <h2>Contenu droite pour proche de vous</h2>
        </div>
      </section>
      <section id='near' class='hero-story hero second-hero hero4'>
        <div class='left-col width-66 delayed'>
          <h2>Contenu pour proche de tout</h2>
        </div>
        <div class='right-col width-33'>
          <h2>Proche de tout</h2>
        </div>
      </section>
      <footer class='hero-story hero second-hero hero5'>
        <h2>Contenu du footer</h2>
      </footer>
      Resources
    </>
  );
};

export default Intro1;
