import "./styles.css";
import React from "react";
import { FormattedMessage } from "react-intl";
import ReactAnime from "react-animejs";
import { Controller, Scene } from "react-scrollmagic";
import { Tween, Timeline } from "react-gsap";
import Lottie from "react-lottie";
import * as animationData from "../../assets/animations/developer.json";

const Intro1 = (props) => {
  const { Anime, stagger } = ReactAnime;

  const defaultOptions = {
    loop: true,
    autoplay: true,
    animationData: animationData,
    rendererSettings: {
      preserveAspectRatio: "xMidYMid slice",
    },
  };

  return (
    <Controller>
      <div className='vh-100 vw-100 d-flex flex-column align-items-center justify-content-center'>
        <div className='container'>
          <div style={{ textJustify: "inter-word", textAlign: "left" }}>
            <Scene duration={500} offset={-100}>
              <Tween
                stagger={0.1}
                from={{
                  rotationX: -90,
                  ease: "elastic",
                }}
                to={{
                  rotationX: 0,
                  ease: "elastic",
                }}
              >
                <h1
                  style={{
                    fontWeight: "800",
                    fontSize: "4vw",
                    textShadow: "0px 0px 10px rgba(47,53,66,0.3)",
                  }}
                >
                  <FormattedMessage id='helloIm' />
                </h1>
              </Tween>
            </Scene>

            <Scene duration={800} offset={-650}>
              <Tween
                stagger={0.1}
                from={{
                  scale: 5,
                  opacity: 0,
                }}
                to={{
                  scale: 1,
                  opacity: 1,
                }}
              >
                <h1
                  style={{
                    fontWeight: "800",
                    fontSize: "14vw",
                    marginBottom: "-5vw",
                    textShadow: "0px 0px 10px rgba(47,53,66,0.3)",
                    marginLeft: "-0.35vw",
                  }}
                >
                  Mohsin
                </h1>
              </Tween>
            </Scene>
            <h1
              style={{
                fontWeight: "800",
                fontSize: "8vw",
                textShadow: "0px 0px 10px rgba(47,53,66,0.3)",
              }}
            >
              Kamal Akbar
            </h1>
            <br></br>
            <div
              className='d-flex justify-content-center align-items-center text-align-center'
              style={{
                width: "150px",
                border: "1px solid #3742fa",
                borderRadius: "10px",
              }}
            >
              <p>Resume.pdf</p>
            </div>
            <Lottie
              options={defaultOptions}
              height={400}
              width={400}
              isStopped={false}
            />
          </div>
        </div>
      </div>
    </Controller>
  );
};

export default Intro1;
