import "./styles.css";
import React from "react";
import { FormattedMessage } from "react-intl";
import ReactAnime from "react-animejs";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowUp } from "@fortawesome/free-solid-svg-icons";

const BackToTop = (props) => {
  const { anime, stagger } = ReactAnime;

  function animateButton(el, scale, duration, elasticity) {
    anime.remove(el);
    anime({
      targets: el,
      scale: scale,
      duration: duration,
      elasticity: elasticity,
    });
  }

  function enterButton(el) {
    animateButton(el, 1.2, 800, 400);
  }

  function leaveButton(el) {
    animateButton(el, 1.0, 600, 300);
  }

  document.getElementById("btn-goup").addEventListener(
    "mouseenter",
    function (e) {
      enterButton(e.target);
    },
    false
  );

  document.getElementById("btn-goup").addEventListener(
    "mouseleave",
    function (e) {
      leaveButton(e.target);
    },
    false
  );

  return (
    <div
      className='d-flex btn-goup justify-content-center align-items-center'
      id='btn-goup'
    >
      <FontAwesomeIcon icon={faArrowUp} color='#FFFFFF' />
    </div>
  );
};

export default BackToTop;
