import "./styles.css";
import React from "react";
import { FormattedMessage } from "react-intl";
import ReactAnime from "react-animejs";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronUp, faChevronDown } from "@fortawesome/free-solid-svg-icons";

const ScrollUp = (props) => {
  const { Anime, stagger } = ReactAnime;
  return (
    <div className='vh-100 d-flex flex-column align-items-center justify-content-center fade-in'>
      <Anime
        initial={[
          {
            targets: "#arrowUp",
            keyframes: [
              {
                translateY: 0,
                delay: 1300,
              },
              {
                translateY: -20,
                delay: 1300,
              },
              {
                translateY: 0,
              },
            ],
            easing: "spring",
            duration: 2000,
            loop: true,
          },
        ]}
      >
        <FontAwesomeIcon
          icon={faChevronUp}
          className='faIcon'
          id='arrowUp'
          opacity={0.5}
          style={{ marginBottom: "40px" }}
        />
      </Anime>
      <div style={{ textJustify: "inter-word" }}>
        <h1
          style={{ fontWeight: "800", fontSize: "48px", marginBottom: "-40px" }}
        >
          Scroll
        </h1>
        <br></br>
        <h1 style={{ fontWeight: "800", marginBottom: "-40px" }}>To See</h1>
        <br></br>
        <h1 style={{ fontWeight: "800", fontSize: "48px" }}>MORE</h1>
      </div>
      <Anime
        initial={[
          {
            targets: "#arrowDown",
            keyframes: [
              {
                translateY: 0,
                delay: 1300,
              },
              {
                translateY: 20,
                delay: 1300,
              },
              {
                translateY: 0,
              },
            ],
            easing: "spring",
            duration: 2000,
            loop: true,
          },
        ]}
      >
        <FontAwesomeIcon
          icon={faChevronDown}
          className='faIcon'
          id='arrowDown'
          opacity={0.5}
          style={{ marginTop: "40px" }}
        />
      </Anime>
    </div>
  );
};

export default ScrollUp;
