import React from "react";
import styled from "styled-components";
import { Controller, Scene } from "react-scrollmagic";
import { Back } from "gsap/gsap-core";
import { Tween, Timeline } from "react-gsap";

const ListStyled = styled.div`
  overflow: hidden;
  .section {
    height: 70vh;
  }
  .devs,
  .devs2 {
    perspective: 4000px;
    left: 400px;
    position: relative;
    & li {
      font-size: 30px;
    }
  }
`;

const Intro2 = () => (
  <ListStyled>
    <div className='section' />
    <Controller>
      <Scene duration={200} offset={400} pin>
        <Tween
          stagger={0.1}
          from={{
            rotationX: -90,
            transformOrigin: "0% ",
          }}
          to={{
            rotationX: 0,
            transformOrigin: "100%",
          }}
        >
          <li>Rich Harris</li>
          <li>Dan Abramov</li>
          <li>Kyle Simpson</li>
          <li>Gregory Brown</li>
          <li>Addy Osmani</li>
          <li>Evan You</li>
          <li>Axel Rauschmayer</li>
          <li>Sarah Drasner</li>
          <li>Andr√© Staltz</li>
        </Tween>
      </Scene>
    </Controller>
  </ListStyled>
);

export default Intro2;
