import "./styles.css";
import React from "react";
import { FormattedMessage } from "react-intl";
import ReactAnime from "react-animejs";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronUp, faChevronDown } from "@fortawesome/free-solid-svg-icons";

const ScrollUp = (props) => {
  const { Anime, stagger } = ReactAnime;
  return (
    <div className='vh-100 d-flex flex-column align-items-center justify-content-center fade-in'>
      <Anime
        initial={[
          {
            targets: "#arrowUp",
            keyframes: [
              {
                translateY: 0,
                delay: 1300,
              },
              {
                translateY: -20,
                delay: 1300,
              },
              {
                translateY: 0,
              },
            ],
            easing: "spring",
            duration: 2000,
            loop: true,
          },
        ]}
      >
        <FontAwesomeIcon
          icon={faChevronUp}
          className='faIcon'
          id='arrowUp'
          opacity={0.5}
          style={{ marginBottom: "40px" }}
        />
      </Anime>
      <Anime
        initial={[
          {
            targets: "#upShadow",
            keyframes: [
              {
                width: 0,
                delay: 1300,
              },
              {
                width: 40,
                delay: 1300,
              },
              {
                width: 0,
              },
            ],
            easing: "spring",
            duration: 2000,
            loop: true,
          },
        ]}
      >
        <div
          id='#upShadow'
          style={{
            width: "20px",
            height: "0px",
            borderRadius: "100px",
            boxShadow: "0 0 5px 2px rgba(0,0,0,0.4)",
            backgroundColor: "rgba(0,0,0,0.4)",
          }}
        ></div>
      </Anime>
      <FormattedMessage id={"scrollToSeeMore"} />
      <Anime
        initial={[
          {
            targets: "#arrowDown",
            keyframes: [
              {
                translateY: 0,
                delay: 1300,
              },
              {
                translateY: 20,
                delay: 1300,
              },
              {
                translateY: 0,
              },
            ],
            easing: "spring",
            duration: 2000,
            loop: true,
          },
        ]}
      >
        <FontAwesomeIcon
          icon={faChevronDown}
          className='faIcon'
          id='arrowDown'
          opacity={0.5}
          style={{ marginTop: "40px" }}
        />
      </Anime>
    </div>
  );
};

export default ScrollUp;
