import "./styles.css";
import React from "react";
import { FormattedMessage } from "react-intl";
import ReactAnime from "react-animejs";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronUp, faChevronDown } from "@fortawesome/free-solid-svg-icons";

const Intro1 = (props) => {
  const { Anime, stagger } = ReactAnime;
  return (
    <div className='vh-100 vw-100 d-flex flex-column align-items-center justify-content-center fade-in'>
      <div className='container'>
        <div style={{ textJustify: "inter-word", textAlign: "left" }}>
          <h1
            style={{
              fontWeight: "800",
              fontSize: "4vw",
              textShadow: "0px 0px 10px rgba(47,53,66,0.3)",
            }}
          >
            Hi, I'm
          </h1>
          <h1
            style={{
              fontWeight: "800",
              fontSize: "14vw",
              marginBottom: "-5vw",
              textShadow: "0px 0px 10px rgba(47,53,66,0.3)",
              marginLeft: "-0.35vw",
            }}
          >
            Mohsin
          </h1>
          <h1
            style={{
              fontWeight: "800",
              fontSize: "8vw",
              textShadow: "0px 0px 10px rgba(47,53,66,0.3)",
            }}
          >
            Kamal Akbar
          </h1>
          <br></br>
          <div
            className='d-flex justify-content-center align-items-center text-align-center'
            style={{
              width: "150px",
              border: "1px solid #3742fa",
              borderRadius: "10px",
            }}
          >
            <p>Resume.pdf</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Intro1;
