import "./styles.css";
import React from "react";
import { FormattedMessage } from "react-intl";
import ReactAnime from "react-animejs";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronUp, faChevronDown } from "@fortawesome/free-solid-svg-icons";

const ScrollUp = (props) => {
  const { Anime, stagger } = ReactAnime;
  return (
    <div className='vh-100 d-flex flex-column align-items-center justify-content-center fade-in'>
      <Anime
        initial={[
          {
            targets: "#arrowUp",
            keyframes: [
              {
                translateY: 0,
              },
              {
                translateY: -30,
                delay: 1300,
              },
              {
                translateY: 0,
              },
            ],
            easing: "spring",
            duration: 2000,
            loop: true,
          },
        ]}
      >
        <FontAwesomeIcon icon={faChevronUp} className='faIcon' id='arrowUp' />
      </Anime>
      <p style={{ marginTop: "15px" }}>Scroll to see more...</p>
      <Anime
        initial={[
          {
            targets: "#arrowDown",
            keyframes: [
              {
                translateY: 0,
              },
              {
                translateY: 30,
                delay: 1300,
              },
              {
                translateY: 0,
              },
            ],
            easing: "spring",
            duration: 2000,
            loop: true,
          },
        ]}
      >
        <FontAwesomeIcon
          icon={faChevronDown}
          className='faIcon'
          id='arrowDown'
        />
      </Anime>
    </div>
  );
};

export default ScrollUp;
