import "./App.css";
import React from "react";
import { IntlProvider } from "react-intl";
import { connect } from "react-redux";
import { BrowserRouter as Router } from "react-router-dom";
import Navbar from "../components/Navbar";
import Routes from "../Routes";
import BackToTop from "../components/BackToTop";
import ReactAnime from "react-animejs";

const App = (props) => {
  const { Anime, stagger } = ReactAnime;

  const renderCircles = (x, y) => {
    return <p style={{ position: "absolute", top: y, left: x }}>CLICKKKK</p>;
  };

  // window.addEventListener("click", (e) => {
  //   var cursorX = e.pageX;
  //   var cursorY = e.pageY;
  //   console.log("CursorX: ", cursorX, "-----", "CursorY: ", cursorY);
  //   renderCircles(cursorX, cursorY);
  // });

  function clickEffect(e) {
    var d = document.createElement("div");
    d.className = "clickEffect";
    d.style.top = e.clientY + "px";
    d.style.left = e.clientX + "px";
    document.body.appendChild(d);
    d.addEventListener(
      "animationend",
      function () {
        d.parentElement.removeChild(d);
      }.bind(this)
    );
  }
  document.addEventListener("click", clickEffect);

  return (
    <IntlProvider messages={require(`../translations/${props.lang}.json`)}>
      <Router>
        <Navbar />
        <BackToTop />
        <Routes />
      </Router>
    </IntlProvider>
  );
};

const mapStateToProps = (state) => {
  return {
    lang: state.languageReducer.lang,
  };
};

export default connect(mapStateToProps, null)(App);
