import "./styles.css";
import React from "react";
import { FormattedMessage } from "react-intl";
import ReactAnime from "react-animejs";
import * as color from "dominant-color";
import breakingBingo from "../../assets/images/breakingBingo.png";

const Card = (props) => {
  const { anime, stagger } = ReactAnime;
  const options = {
    scale: 2,
    max: 90,
    glare: true,
  };
  return (
    <div className='card container' options={options}>
      <div className='cardContent'>
        <h1>{props.title}</h1>
      </div>
    </div>
  );
};

export default Card;
