import "./styles.css";
import React, { useEffect, useRef } from "react";
import { FormattedMessage } from "react-intl";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { gsap } from "gsap";
import Lottie from "react-lottie";
import animationData from "../../assets/animations/developer.json";
import { Timeline } from "gsap/gsap-core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFileDownload } from "@fortawesome/free-solid-svg-icons";
import { saveAs } from "file-saver";

const Intro1 = (props) => {
  const defaultOptions = {
    loop: true,
    autoplay: true,
    animationData: animationData,
  };

  const sectionRef = useRef([]);

  const handleDownload = () => {
    //Use the commented part only when you get the CONTENT of the file instead of the whole file...
    // let blob = new Blob([require("../../assets/documents/CV.docx")], {
    //   type: "application/vnd.openxmlformats-officedocument.wordprocessingml.document'",
    // });
    saveAs(
      require("../../assets/documents/resume.pdf"),
      "CV - Mohsin Kamal Akbar.pdf"
    );
  };

  useEffect(() => {
    gsap.from(sectionRef.current, {
      duration: 1,
      autoAlpha: 0,
      ease: "none",
      dealy: 1,
    });
  }, [sectionRef]);

  const addToRef = (el) => {};

  return (
    <div
      // style={{ height: "100vh" }}
      className=' vw-100 d-flex flex-column align-items-center justify-content-center overflow-hidden'
    >
      {/* SECTION 1 START */}
      <section className='container-fluid row hero' ref={addToRef}>
        <div className='col-md-7'>
          <div style={{ textJustify: "inter-word", textAlign: "left" }}>
            <h1
              className='hiIm'
              style={{
                fontWeight: "800",
                fontSize: "4vw",
                textShadow: "0px 0px 10px rgba(47,53,66,0.3)",
                userSelect: "none",
              }}
            >
              <FormattedMessage id='helloIm' />
            </h1>
            <h1
              className='mohsin'
              style={{
                fontWeight: "800",
                fontSize: "14vw",
                marginBottom: "-5vw",
                textShadow: "0px 0px 10px rgba(47,53,66,0.3)",
                marginLeft: "-0.35vw",
                userSelect: "none",
              }}
            >
              Mohsin
            </h1>
            <h1
              className='kamalakbar'
              style={{
                fontWeight: "800",
                fontSize: "8vw",
                textShadow: "0px 0px 10px rgba(47,53,66,0.3)",
                userSelect: "none",
              }}
            >
              Kamal Akbar
            </h1>
            <br></br>
            <div className='download' onClick={handleDownload}>
              <FontAwesomeIcon
                icon={faFileDownload}
                opacity={0.4}
                style={{
                  fontSize: "20px",
                  marginRight: "8px",
                }}
              />
              {"      "}
              Download CV
            </div>
          </div>
        </div>

        <div className='col-md-5 d-flex align-items-start animation'>
          <Lottie
            options={defaultOptions}
            height={500}
            width={400}
            isStopped={false}
            style={{
              marginTop: "-10vh",
              zIndex: 99,
              userSelect: "none",
              cursor: "default",
            }}
          />
        </div>
      </section>
      {/* SECTION 1 END */}
      {/* -------------------------------------------------------------------------------------------------------------------------------------------- */}
      {/* SECTION 2 START */}
      <section className='container-fluid row hero' ref={addToRef}>
        <div className='col-md-5 d-flex align-items-start animation'>
          <Lottie
            options={defaultOptions}
            height={500}
            width={400}
            isStopped={false}
            style={{
              marginTop: "-10vh",
              zIndex: 99,
              userSelect: "none",
              cursor: "default",
            }}
          />
        </div>
        <div className='col-md-7'>
          <div style={{ textJustify: "inter-word", textAlign: "left" }}>
            <h1
              className='hiIm'
              style={{
                fontWeight: "800",
                fontSize: "4vw",
                textShadow: "0px 0px 10px rgba(47,53,66,0.3)",
                userSelect: "none",
              }}
            >
              <FormattedMessage id='helloIm' />
            </h1>
            <h1
              className='mohsin'
              style={{
                fontWeight: "800",
                fontSize: "14vw",
                marginBottom: "-5vw",
                textShadow: "0px 0px 10px rgba(47,53,66,0.3)",
                marginLeft: "-0.35vw",
                userSelect: "none",
              }}
            >
              Mohsin
            </h1>
            <h1
              className='kamalakbar'
              style={{
                fontWeight: "800",
                fontSize: "8vw",
                textShadow: "0px 0px 10px rgba(47,53,66,0.3)",
                userSelect: "none",
              }}
            >
              Kamal Akbar
            </h1>
            <br></br>
            <div className='download' onClick={handleDownload}>
              <FontAwesomeIcon
                icon={faFileDownload}
                opacity={0.4}
                style={{
                  fontSize: "20px",
                  marginRight: "8px",
                }}
              />
              {"      "}
              Download CV
            </div>
          </div>
        </div>
      </section>
      {/* SECTION 2 END */}
    </div>
  );
};

export default Intro1;
